import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Home from './pages/Home'
import Cart from './pages/CartShop';
import Product from './pages/Product';
import Catalog from './pages/Catalog';
import ResultCatalog from './pages/ResultCatalog';
import { Mailer } from 'nodemailer-react'
import { PasswordEmail } from './PasswordEmail'
import { ReminderEmail } from './ReminderEmail'

const mailerConfig = {
  transport: {
    host: 'smtp.example.com',
    secure: true,
    auth: { user: 'username', pass: 'password' },
  },
  defaults: {
    from: { name: 'mathieutu', address: 'oss@mathieutu.dev' },
  },
}

const emailsList = {
  PasswordEmail,
  ReminderEmail,
}

const mailer = Mailer(mailerConfig, emailsList)

await mailer.send('PasswordEmail', {
  firstName: 'Mathieu',
  brand: 'MyWebsite',
  newAccount: true,
  password: Math.random().toString(36).substring(7),
}, {
  to: 'foo@bar.fr',
  attachments: [{ content: 'bar', filename: 'foo.txt' }],
})

await mailer.send('ReminderEmail', {
  firstName: 'Mathieu',
  task: 'Write package documentation!',
}, {
  to: 'foo@bar.fr',
})

export const CartContext = React.createContext();

const App = () => {
  const [cartItems, setCartItems] = React.useState([]);
  const [productItem, setProductItem] = React.useState([]);
  const [counter, setCounter] = React.useState(1);

  function onAddToCart(obj) {
    setCartItems([...cartItems, obj]);
  }

  function showProduct(item) {
    setProductItem(item);
  }

  function clearCart() {
    setCartItems([]);
    setCounter(1);
  }

  function decrementCounter() {
    setCounter(counter - 1);

    if(counter <= 1){
        setCounter(1);
    }
  }

  function incrementCounter() {
    setCounter(counter + 1);
    
    if(counter >= 99){
        setCounter(99);
    }
  }

  return(
      <CartContext.Provider value={{
        cartItems,
        productItem,
        onAddToCart,
        clearCart,
        decrementCounter,
        incrementCounter,
        showProduct,
        counter
      }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="cart" element={<Cart />} />
          <Route path="product" element={<Product />} />
          <Route path="catalog" element={<Catalog />} />
          <Route path="result" element={<ResultCatalog />} />
        </Routes>
      </CartContext.Provider>
  )
}

export default App;
