import React from "react";

import arrowDownIcon from "../../icons/arrow-down.svg"

const CatalogItem = ({ title, newItems}) => {
    const [isActive, setIsActive] = useState(false);
    return(
        <>
            <button onClick="" className="catalog-item">
                <h3 className="title">Школьные</h3>
                <img width={18} height={18} src={arrowDownIcon} alt="arrow" />
            </button>
            <button onClick="" className="catalog-item">
                <h3 className="title">Праздничные</h3>
                <img className={true ? " arrow active": "arrow"} width={18} height={18} src={arrowDownIcon} alt="arrow" />
            </button>
        </>
    )
}

export default CatalogItem;
