import React from 'react'
import HeaderNewItems from './HeaderNewItems';
import { CartContext } from './../../App';
import Item from './Item.js'; 

export default function Items() {
    const { onAddToCart, showProduct } = React.useContext(CartContext);
    const [products, setProducts] = React.useState([]);

    const obj = 
        
        {
            "name": "Белые банты 2 шт",
            "price": "180",
            "fileName": "img/band-white.webp",
            "type": "Банты",
            "purpose": "Праздничный",
            "material": "Двусторонняя замша",
            "size": "10х10"
        }
    

    const jsonObj = JSON.stringify(obj)
     
    React.useEffect(() => {
         fetch('http://localhost:8080/getAllProducts')
         .then((res) => {
             return res.json();
         })
         .then(json => {
             setProducts(json);
             console.log(json)
         });
    
        async function saveData() {
            let response = await fetch('http://localhost:8080/saveProduct', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify(obj)
            });
        }
    
        // saveData()
    }, []); 

    console.log(products)

    return (
         <div className="new-items">
             <HeaderNewItems />
             <ul className="slides">
                 {products.map((item, index) => {
                     return (
                         <Item
                         key={index}
                         img={item.img} 
                         alt={item.title} 
                         title={item.title}
                         price={item.price}
                         onPlus={(obj) => {onAddToCart(obj)}}
                         onProduct={(obj) => {showProduct(obj)}} />
                     )
                 })}
             </ul>
         </div>
    );
}
