import React from "react";
import { useState } from "react";
import arrowDownIcon from "../../icons/arrow-down.svg"

const CatalogItem = ({ title, newItems}) => {
    const [isActive, setIsActive] = useState(false);
    return(
        <div className={isActive ? "catalog-item active" : ""}>
            <button onClick={() => setIsActive(!isActive)} className="catalog-button">
                <h3 className="title">Школьные</h3>
                <img className={isActive ? "arrow active" : "arrow"} width={18} height={18} src={arrowDownIcon} alt="arrow" />
            </button>
            {isActive && <div className="content">ррр</div>}
        </div>
    )
}

export default CatalogItem;
