import React from 'react'
import HeaderNewItems from './HeaderNewItems';
import { CartContext } from '../../App';
import Item from './Item.jsx'; 

export default function Items() {
    const { onAddToCart, showProduct } = React.useContext(CartContext);
    const [products, setProducts] = React.useState([]);
    const [newItems, setNewItems] = React.useState([]);

    const obj = {
            "name": "Белые банты 2 шт",
            "price": "180",
            "fileName": "img/band-white.webp",
            "type": "Банты",
            "purpose": "Праздничный",
            "material": "Двусторонняя замша",
            "size": "10х10"
        }
     
    React.useEffect(() => {
        fetch('http://localhost:8080/getAllProducts')
        .then((res) => {
            return res.json();
        })
        .then(json => {
            setProducts(json);
        });

        fetch('http://localhost:8080/filterByDate')
        .then((res) => {
            return res.json();
        })
        .then(json => {
            setNewItems(json);
        });
    
        async function saveData() {
            await fetch('http://localhost:8080/saveProduct', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify(obj)
            });
        }
    
        // saveData()
    }, []); 
    

    return (
         <div className="new-items">
             <HeaderNewItems />
             <ul className="slides">
                 {products.map((item, index) => {
                     return (
                         <Item
                         key={index}
                         img={item.fileName} 
                         alt={item.name} 
                         title={item.name}
                         price={item.price}
                         onPlus={(obj) => {onAddToCart(obj)}}
                         onProduct={(obj) => {showProduct(obj)}} />
                     )
                 })}
             </ul>
         </div>
    );
}
